mapper.insert:
insert into registers ( register_patient_id, register_user_id, register_regdate, register_date, register_memo, register_communication, register_state) values ( #{register_patient_id}, #{register_user_id}, now(), #{register_date}, #{register_memo}, #{register_communication},
"대기"
#{register_state}
)

mapper.select:
select count(*) from registers
where
register_date=#{register_date}
and register_user_id=#{register_user_id}

select count(*) from registers
where
register_date=#{register_date}
and register_user_id=#{register_user_id}
and register_patient_id=#{register_patient_id}

mapper.update:
update registers
set
register_starttime=now(),
register_state=#{register_state}
where
register_id=#{register_id}
